import React, { useEffect, useState } from 'react';
import axiosInstance from './axiosInstance';

import UserHeader from './UserHeader';
import Map from '../Map';

function UserHome() {
  const [villes, setVilles] = useState([]);
  const [selectedVille, setSelectedVille] = useState('');
  const [zones, setZones] = useState([]);
  const [selectedZone, setSelectedZone] = useState('');
  const [specialities, setSpecialities] = useState([]);

  useEffect(() => {
    // Fetch all villes
    const fetchVilles = async () => {
      try {
        const response = await axiosInstance.get('/api/villes');
        const fetchedVilles = response.data;
        setVilles(fetchedVilles);
      } catch (error) {
        console.error('Error fetching villes:', error);
      }
    };

    fetchVilles();
  }, []);

  useEffect(() => {
    // Fetch zones based on the selected ville
    const fetchZones = async () => {
      try {
        const response = await axiosInstance.get(`/api/zones/by-ville?villeName=${selectedVille}`);
        const fetchedZones = response.data;
        setZones(fetchedZones);
      } catch (error) {
        console.error('Error fetching zones:', error);
      }
    };

    if (selectedVille) {
      fetchZones();
    } else {
      setZones([]);
    }
  }, [selectedVille]);

  useEffect(() => {
    // Fetch all specialities
    const fetchSpecialities = async () => {
      try {
        const response = await axiosInstance.get('/api/specialities');
        const fetchedSpecialities = response.data;
        setSpecialities(fetchedSpecialities);
      } catch (error) {
        console.error('Error fetching specialities:', error);
      }
    };

    fetchSpecialities();
  }, []);

  const handleVilleChange = (event) => {
    setSelectedVille(event.target.value);
    setSelectedZone('');
  };

  const handleZoneChange = (event) => {
    setSelectedZone(event.target.value);
  };

  return (
    <div>
      <UserHeader />
      <div>
        <label htmlFor="ville">Ville:</label>
        <select id="ville" value={selectedVille} onChange={handleVilleChange}>
          <option value="">Select a ville</option>
          {villes.map((ville) => (
            <option key={ville.id} value={ville.name}>{ville.name}</option>
          ))}
        </select>
      </div>

      {selectedVille && (
        <div>
          <label htmlFor="zone">Zone:</label>
          <select id="zone" value={selectedZone} onChange={handleZoneChange}>
            <option value="">Select a zone</option>
            {zones.map((zone) => (
              <option key={zone.id} value={zone.name}>{zone.name}</option>
            ))}
          </select>
        </div>
      )}

      <div>
        <label htmlFor="speciality">Speciality:</label>
        <select id="speciality">
          <option value="">Select a speciality</option>
          {specialities.map((speciality) => (
            <option key={speciality.id} value={speciality.name}>{speciality.name}</option>
          ))}
        </select>
      </div>

      <Map />
    </div>
  );
}

export default UserHome;
