import React, { useEffect, useState } from "react";
import { Table, Form } from "react-bootstrap";
import axiosInstance from "../axiosInstance";
import AdminHeader from "./AdminHeader";

function Zones() {
  const [cities, setCities] = useState([]);
  const [selectedCity, setSelectedCity] = useState("");
  const [zones, setZones] = useState([]);

  useEffect(() => {
    fetchCities();
  }, []);

  const fetchCities = async () => {
    try {
      const response = await axiosInstance.get("/api/villes");
      const data = response.data;
      setCities(data);
    } catch (error) {
      console.error("Error fetching cities:", error);
    }
  };

  const fetchZonesByVille = async (villeName) => {
    try {
      const response = await axiosInstance.get("/api/zones/by-ville", {
        params: {
          villeName: villeName,
        },
      });
      const data = response.data;
      setZones(data);
    } catch (error) {
      console.error("Error fetching zones:", error);
    }
  };

  const handleCityChange = (e) => {
    const selectedCity = e.target.value;
    setSelectedCity(selectedCity);
    fetchZonesByVille(selectedCity);
  };

  return (
    <>
      <AdminHeader />

      <div className="container">
        <h1 className="mt-3" style={{ fontFamily: "cursive" }}>
          Zones
        </h1>

        <div>
          <Form.Group>
            <Form.Label>Select a city:</Form.Label>
            <Form.Control as="select" onChange={handleCityChange}>
              <option value="">Select a city</option>
              {cities.map((city) => (
                <option key={city.id} value={city.nom}>
                  {city.nom}
                </option>
              ))}
            </Form.Control>
          </Form.Group>
        </div>

        {zones.length > 0 && (
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                {/* Add more columns as needed */}
              </tr>
            </thead>
            <tbody>
              {zones.map((zone) => (
                <tr key={zone.id}>
                  <td>{zone.id}</td>
                  <td>{zone.nom}</td>
                  {/* Render additional zone properties as table cells */}
                </tr>
              ))}
            </tbody>
          </Table>
        )}
      </div>
    </>
  );
}

export default Zones;
