import React, { useEffect, useRef } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import axiosInstance from './axiosInstance';

const Map = () => {
  const mapRef = useRef(null);

  useEffect(() => {
    // Create the map instance
    const map = L.map(mapRef.current);

    // Add the tile layer from OpenStreetMap
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: 'Map data © OpenStreetMap contributors',
    }).addTo(map);

    // Fetch restaurant data from the backend
    const fetchRestaurants = async () => {
      try {
        const response = await axiosInstance.get('/api/restaurants');
        const restaurants = response.data;

        // Iterate over the restaurants and add markers to the map
        restaurants.forEach((restaurant) => {
          const { latitude, longitude } = restaurant;

          // Create a marker and add it to the map
          L.marker([latitude, longitude]).addTo(map);
        });
      } catch (error) {
        console.error('Error fetching restaurants:', error);
      }
    };

    fetchRestaurants();

    // Get the user's current location
    if ('geolocation' in navigator) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;

          // Set the map view to the user's current location
          map.setView([latitude, longitude], 13);
        },
        (error) => {
          console.error('Error getting current location:', error);
          // Set the default center if user location cannot be obtained
          map.setView([51.505, -0.09], 13);
        }
      );
    } else {
      // Set the default center if geolocation is not supported
      map.setView([51.505, -0.09], 13);
    }

    return () => {
      // Clean up the map when the component is unmounted
      map.remove();
    };
  }, []);

  return <div ref={mapRef} style={{ width: '100%', height: '400px' }} />;
};

export default Map;
