import React, { useEffect, useState } from "react";
import { Table, Form, Button, Modal } from "react-bootstrap";
import axiosInstance from "../axiosInstance";


function Specialities() {
    const [specialities, setSpecialities] = useState([]);
    const [newSpeciality, setNewSpeciality] = useState("");

    useEffect(() => {
        fetchSpecialities();
    }, []);

    const fetchSpecialities = async () => {
        try {
            const response = await axiosInstance.get("/api/specialities");
            const data = response.data;
            setSpecialities(data);
        } catch (error) {
            console.error("Error fetching specialities:", error);
        }
    };

    const handleAddSpeciality = async (e) => {
        e.preventDefault();
        if (newSpeciality.trim() !== "") {
            try {
                const speciality = {
                    nom: newSpeciality,
                };
                await axiosInstance.post("/api/specialities", speciality);
                setNewSpeciality("");
                fetchSpecialities();
            } catch (error) {
                console.error("Error adding speciality:", error);
            }
        }
    };

    const handleDeleteSpeciality = async (speciality) => {
        try {
            await axiosInstance.delete(`/api/specialities/${speciality.id}`);
            setSpecialities((prevSpecialities) =>
                prevSpecialities.filter((s) => s.id !== speciality.id)
            );
        } catch (error) {
            console.error("Error deleting speciality:", error);
        }
    };

    const handleEditSpeciality = (speciality) => {
        // ...handle edit logic
    };

    return (
        <div>
            <AdminHeader />
            <div className="container">
                <h1 className="mt-3" style={{ fontFamily: "cursive" }}>
                    Liste des spécialités
                </h1>
                <div className="container mb-4">
                    <h4>Ajouter une spécialité</h4>
                    <Form onSubmit={handleAddSpeciality}>
                        <div className="row">
                            <div className="col-md-6">
                                <Form.Group>
                                    <Form.Control
                                        type="text"
                                        value={newSpeciality}
                                        onChange={(e) => setNewSpeciality(e.target.value)}
                                    />
                                </Form.Group>
                            </div>
                        </div>
                        <Button className="btn btn-success mt-2 mb-2" type="submit">
                            Add Speciality
                        </Button>
                    </Form>
                </div>

                {/* Render the specialities table */}
                {specialities.length > 0 && (
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Name</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {specialities.map((speciality) => (
                                <tr key={speciality.id}>
                                    <td>{speciality.id}</td>
                                    <td>{speciality.nom}</td>
                                    <td>
                                        <Button
                                            onClick={() => handleEditSpeciality(speciality)}
                                            style={{ backgroundColor: "#F28135" }}
                                        >
                                            Edit
                                        </Button>{" "}
                                        <Button
                                            variant="danger"
                                            onClick={() => handleDeleteSpeciality(speciality)}
                                        >
                                            Delete
                                        </Button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                )}
            </div>
            </div >
            );
}

            export default Specialities;
