import React, { useEffect, useState } from 'react';
import axiosInstance from '../axiosInstance';
import AdminHeader from './AdminHeader';

const Restaurants = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedVille, setSelectedVille] = useState('');
  const [selectedZone, setSelectedZone] = useState('');
  const [villes, setVilles] = useState([]);
  const [zones, setZones] = useState([]);

  useEffect(() => {
    fetchVilles();
    fetchRestaurants();
  }, []);

  const fetchVilles = async () => {
    try {
      const response = await axiosInstance.get('/api/villes');
      setVilles(response.data);
    } catch (error) {
      console.error('Error fetching villes:', error);
    }
  };

  const fetchZonesByVille = async (villeName) => {
    try {
      const response = await axiosInstance.get(`/api/zones/by-ville?villeName=${villeName}`);
      setZones(response.data);
    } catch (error) {
      console.error('Error fetching zones:', error);
    }
  };

  const fetchRestaurants = async () => {
    try {
      let url = '/api/restaurants';
      if (selectedVille) {
        url += `?ville=${selectedVille}`;
        if (selectedZone) {
          url += `&zone=${selectedZone}`;
        }
      }
      const response = await axiosInstance.get(url);
      setRestaurants(response.data);
    } catch (error) {
      console.error('Error fetching restaurants:', error);
    }
  };

  const handleSelectVille = (e) => {
    const ville = e.target.value;
    setSelectedVille(ville);
    setSelectedZone('');
    // Fetch zones based on selected ville
    fetchZonesByVille(ville);
    // Fetch restaurants based on selected ville
    fetchRestaurants();
  };

  const handleSelectZone = (e) => {
    const zone = e.target.value;
    setSelectedZone(zone);
    // Fetch restaurants based on selected ville and zone
    fetchRestaurants();
  };

  return (
    <div>
      <AdminHeader></AdminHeader>
      <div>
        <h2>Ville List</h2>
        <select value={selectedVille} onChange={handleSelectVille}>
          <option value="">Select a ville</option>
          {villes.map((ville) => (
            <option key={ville.id} value={ville.nom}>
              {ville.nom}
            </option>
          ))}
        </select>
      </div>
      {selectedVille && (
        <div>
          <h2>Zone List</h2>
          <select value={selectedZone} onChange={handleSelectZone}>
            <option value="">Select a zone</option>
            {zones.map((zone) => (
              <option key={zone.id} value={zone.nom}>
                {zone.nom}
              </option>
            ))}
          </select>
        </div>
      )}
      <h2>Restaurant List</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Address</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {restaurants.map((restaurant) => (
            <tr key={restaurant.id}>
              <td>{restaurant.nom}</td>
              <td>{restaurant.adresse}</td>
              <td>
                <button>Edit</button>
                <button>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Restaurants;
