import React, { useEffect, useState } from "react";
import { Table, Button } from "react-bootstrap";
import axiosInstance from "../axiosInstance";
import AdminHeader from "./AdminHeader";

function Cities() {
  const [cities, setCities] = useState([]);

  useEffect(() => {
    fetchCities();
  }, []);

  const fetchCities = async () => {
    try {
      const response = await axiosInstance.get("/api/villes");
      const data = response.data;
      setCities(data);
    } catch (error) {
      console.error("Error fetching cities:", error);
    }
  };

  const handleDeleteCity = async (cityId) => {
    try {
      await axiosInstance.delete(`/api/villes/${cityId}`);
      fetchCities();
    } catch (error) {
      console.error("Error deleting city:", error);
    }
  };

  const handleUpdateCity = (cityId) => {
    // Implement your logic for handling the update action here
    // For example, you can navigate to a separate update city page
    // or open a modal to edit the city details
    console.log("Update city:", cityId);
  };

  return (
    <div>
      <AdminHeader />
      <h1 class="mt-3" style={{ fontFamily: "cursive" }}>
        Liste des villes
      </h1>
      <Table striped bordered hover>
        <thead style={{ backgroundColor: "#9C9C9C" }}>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {cities.map((city) => (
            <tr key={city.id}>
              <td>{city.id}</td>
              <td>{city.nom}</td>
              <td>
                <Button
                  variant="danger"
                  onClick={() => handleDeleteCity(city.id)}
                >
                  Delete
                </Button>
                <Button
                  variant="info"
                  onClick={() => handleUpdateCity(city.id)}
                  className="ms-2"
                >
                  Update
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default Cities;
