import React, { useEffect, useState } from 'react';

import UserHeader from './UserHeader';
import Map from '../Map';
import axiosInstance from '../axiosInstance';

function UserHome() {
  const [villes, setVilles] = useState([]);
  const [selectedVille, setSelectedVille] = useState('');
  const [cities, setCities] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');
  const [specialities, setSpecialities] = useState([]);

  useEffect(() => {
    // Fetch all villes
    const fetchVilles = async () => {
      try {
        const response = await axiosInstance.get('/api/villes');
        const fetchedVilles = response.data;
        setVilles(fetchedVilles);
      } catch (error) {
        console.error('Error fetching villes:', error);
      }
    };

    fetchVilles();
  }, []);

  useEffect(() => {
    // Fetch cities based on the selected ville
    const fetchCities = async () => {
      try {
        const response = await axiosInstance.get(`/api/villes/${selectedVille}/cities`);
        const fetchedCities = response.data;
        setCities(fetchedCities);
      } catch (error) {
        console.error('Error fetching cities:', error);
      }
    };

    if (selectedVille) {
      fetchCities();
    } else {
      setCities([]);
    }
  }, [selectedVille]);

  useEffect(() => {
    // Fetch all specialities
    const fetchSpecialities = async () => {
      try {
        const response = await axiosInstance.get('/api/specialities');
        const fetchedSpecialities = response.data;
        setSpecialities(fetchedSpecialities);
      } catch (error) {
        console.error('Error fetching specialities:', error);
      }
    };

    fetchSpecialities();
  }, []);

  const handleVilleChange = (event) => {
    setSelectedVille(event.target.value);
    setSelectedCity('');
  };

  const handleCityChange = (event) => {
    setSelectedCity(event.target.value);
  };

  return (
    <div>
      <UserHeader />
      <div>
        <label htmlFor="ville">Ville:</label>
        <select id="ville" value={selectedVille} onChange={handleVilleChange}>
          <option value="">Select a ville</option>
          {villes.map((ville) => (
            <option key={ville.id} value={ville.id}>{ville.name}</option>
          ))}
        </select>
      </div>

      {selectedVille && (
        <div>
          <label htmlFor="city">City:</label>
          <select id="city" value={selectedCity} onChange={handleCityChange}>
            <option value="">Select a city</option>
            {cities.map((city) => (
              <option key={city.id} value={city.id}>{city.name}</option>
            ))}
          </select>
        </div>
      )}

      <div>
        <label htmlFor="speciality">Speciality:</label>
        <select id="speciality">
          <option value="">Select a speciality</option>
          {specialities.map((speciality) => (
            <option key={speciality.id} value={speciality.id}>{speciality.name}</option>
          ))}
        </select>
      </div>

      <Map />
    </div>
  );
}

export default UserHome;
