import React, { useEffect, useState } from "react";
import { Table } from "react-bootstrap";
import axiosInstance from "../axiosInstance";

function Zones({ selectedCity }) {
  const [zones, setZones] = useState([]);

  useEffect(() => {
    if (selectedCity) {
      fetchZones(selectedCity.id);
    }
  }, [selectedCity]);

  const fetchZones = async (cityId) => {
    try {
      const response = await axiosInstance.get(`/api/villes/${cityId}/zones`);
      const data = response.data;
      setZones(data);
    } catch (error) {
      console.error("Error fetching zones:", error);
    }
  };

  return (
    <Table striped bordered hover>
      <thead style={{ backgroundColor: "#9C9C9C" }}>
        <tr>
          <th>ID</th>
          <th>Name</th>
        </tr>
      </thead>
      <tbody>
        {zones.map((zone) => (
          <tr key={zone.id}>
            <td>{zone.id}</td>
            <td>{zone.name}</td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
}

export default Zones;
