import React, { useState, useEffect } from 'react'; 
import axios from 'axios'; 
import { Table } from 'react-bootstrap';
import { Pagination } from 'react-bootstrap';
import "../style/city.css"
import 'bootstrap/dist/css/bootstrap.min.css'

const City = () => { 
  // État pour stocker la liste des cities 
  const [cities, setCities] = useState([]);
  
  // État pour stocker le numéro de la page active
  const [activePage, setActivePage] = useState(1);

  // Constante pour stocker le nombre de villes à afficher par page
  const citiesPerPage = 20;

 const [cityName, setCityName] = useState(''); 
 const [cityId, setCityId] = useState('');

  // Fonction pour récupérer la liste des cities depuis le backend 
  const fetchCities = async () => { 
    try { 
      const response = await axios.get('/api/villes'); 
      setCities(response.data); 
    } catch (error) { 
      console.error(error); 
    } 
  }; 

  const getCityById = async (id) => { 
    try { 
    const response = await axios.get(`/api/villes/${id}`); 
    setCityId(response.data.id); 
    setCityName(response.data.name); 
    } catch (error) { 
    console.error(error); 
    } 
   }; 

   const addCity = async () => { 
    try { 
    const response = await axios.post('/api/villes', { name: cityName }); 
    setCities([...cities, response.data]); 
    setCityName(''); 
    } catch (error) { 
    console.error(error); 
    } 
   }; 

   const updateCity = async () => { 
    try { 
    const response = await axios.put(`/api/cities/${cityId}`, { name: cityName }); 
    const updatedCities = cities.map((city) => { 
    if (city.id === response.data.id) { 
    return response.data; 
    } 
    return city; 
    }); 
    setCities(updatedCities); 
    setCityId(''); 
    setCityName(''); 
    } catch (error) { 
    console.error(error); 
    } 
   }; 
  
  // Effet pour charger les cities au montage du composant 
  useEffect(() => { 
    fetchCities(); 
  }, []); 

  // Calcul du nombre de pages
  const pageCount = Math.ceil(cities.length / citiesPerPage);

  // Fonction pour récupérer les villes de la page active
  const getCitiesForPage = () => {
    const startIndex = (activePage - 1) * citiesPerPage;
    const endIndex = startIndex + citiesPerPage;
    return cities.slice(startIndex, endIndex);
  }

  // Fonction pour changer de page
  const handlePageChange = (pageNumber) => {
    setActivePage(pageNumber);
  }

  return ( 
    <div className="city-container">
      <Table striped bordered hover> 
        <thead style={{background : '#9C9C9C'}}> 
          <tr> 
            <th>Numéro</th> 
            <th>Nom</th> 
            {/* Ajoutez d'autres en-têtes de colonne selon vos besoins */} 
          </tr> 
        </thead> 
        <tbody> 
          {getCitiesForPage().map((city, index) => ( 
            <tr key={city.id}> 
              <td>{index + 1}</td> 
              <td>{city.nom}</td> 
              {/* Ajoutez d'autres cellules de données selon vos besoins */} 
            </tr> 
          ))} 
        </tbody> 
      </Table>
      <Pagination className="pagination" >
        {Array.from({ length: pageCount }, (_, index) => index + 1).map(pageNumber => (
          <Pagination.Item key={pageNumber} active={pageNumber === activePage} onClick={() => handlePageChange(pageNumber)}
          >
            {pageNumber}
          </Pagination.Item>
        ))}
      </Pagination>
    </div>
  );
}; 

export default City;
