import React, { useEffect, useState } from 'react';
import axiosInstance from '../axiosInstance';
import AdminHeader from './AdminHeader';

const Restaurants = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedVille, setSelectedVille] = useState('');
  const [selectedZone, setSelectedZone] = useState('');

  useEffect(() => {
    fetchRestaurants();
  }, []);

  const fetchRestaurants = async () => {
    try {
      const response = await axiosInstance.get(`/api/restaurants?ville=${selectedVille}&zone=${selectedZone}`);
      setRestaurants(response.data);
    } catch (error) {
      console.error('Error fetching restaurants:', error);
    }
  };

  const handleVilleChange = (event) => {
    setSelectedVille(event.target.value);
    setSelectedZone(''); // Reset selected zone when ville changes
  };

  const handleZoneChange = (event) => {
    setSelectedZone(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    fetchRestaurants();
  };

  return (
    <div>
      <AdminHeader></AdminHeader>

      <h1>Restaurant Management</h1>

      <form onSubmit={handleSubmit}>
        <label htmlFor="ville">Select Ville:</label>
        <select id="ville" value={selectedVille} onChange={handleVilleChange}>
          {/* Populate with options for villes */}
        </select>

        <label htmlFor="zone">Select Zone:</label>
        <select id="zone" value={selectedZone} onChange={handleZoneChange}>
          {/* Populate with options for zones based on selected ville */}
        </select>

        <button type="submit">Filter</button>
      </form>

      <div className="restaurant-cards">
        {restaurants.map((restaurant) => (
          <div className="restaurant-card" key={restaurant.id}>
            <h2>{restaurant.nom}</h2>
            <p>{restaurant.adresse}</p>
            {/* Display other restaurant details */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Restaurants;
