import React, { useEffect, useState } from 'react';
import axiosInstance from '../axiosInstance';

const Restaurants = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [newRestaurant, setNewRestaurant] = useState({
    nom: '',
    adresse: '',
    longitude: '',
    lattitude: '',
    num_tel: '',
  });
  const [villes, setVilles] = useState([]);
  const [zones, setZones] = useState([]);
  const [selectedVille, setSelectedVille] = useState('');
  const [selectedZone, setSelectedZone] = useState('');

  useEffect(() => {
    fetchRestaurants();
    fetchVilles();
  }, []);

  const fetchRestaurants = async () => {
    try {
      const params = {};
      if (selectedVille) {
        params.villeId = selectedVille;
      }
      if (selectedZone) {
        params.zoneId = selectedZone;
      }
      const response = await axiosInstance.get('/api/restaurants', { params });
      setRestaurants(response.data);
    } catch (error) {
      console.error('Error fetching restaurants:', error);
    }
  };

  const fetchVilles = async () => {
    try {
      const response = await axiosInstance.get('/api/villes');
      setVilles(response.data);
    } catch (error) {
      console.error('Error fetching villes:', error);
    }
  };

  const fetchZones = async (selectedVille) => {
    try {
      const response = await axiosInstance.get(`/api/villes/${selectedVille}/zones`);
      setZones(response.data);
    } catch (error) {
      console.error('Error fetching zones:', error);
    }
  };

  const handleCreateRestaurant = async () => {
    try {
      await axiosInstance.post('/api/restaurants', newRestaurant);
      setNewRestaurant({
        nom: '',
        adresse: '',
        longitude: '',
        lattitude: '',
        num_tel: '',
      });
      fetchRestaurants();
      // Show success message or perform any other action
    } catch (error) {
      console.error('Error creating restaurant:', error);
    }
  };

  const handleUpdateRestaurant = async () => {
    try {
      await axiosInstance.put(`/api/restaurants/${selectedRestaurant.id}`, {
        ...selectedRestaurant,
        villeId: selectedVille,
        zoneId: selectedZone,
      });
      setSelectedRestaurant(null);
      fetchRestaurants();
      // Show success message or perform any other action
    } catch (error) {
      console.error('Error updating restaurant:', error);
    }
  };

  const handleDeleteRestaurant = async (restaurantId) => {
    try {
      await axiosInstance.delete(`/api/restaurants/${restaurantId}`, {
        params: {
          villeId: selectedVille,
          zoneId: selectedZone,
        },
      });
      fetchRestaurants();
      // Show success message or perform any other action
    } catch (error) {
      console.error('Error deleting restaurant:', error);
    }
  };

  const handleSelectRestaurant = (restaurant) => {
    setSelectedRestaurant(restaurant);
  };

  return (
    <div>
      <h1>Restaurant Management</h1>

      {/* Create Restaurant Form */}
      <h2>Create Restaurant</h2>
      <form onSubmit={handleCreateRestaurant}>
        <div>
          <label htmlFor="restaurantName">Name:</label>
          <input
            type="text"
            id="restaurantName"
            value={newRestaurant.nom}
            onChange={(e) => setNewRestaurant({ ...newRestaurant, nom: e.target.value })}
            required
          />
        </div>
        <div>
          <label htmlFor="restaurantAddress">Address:</label>
          <input
            type="text"
            id="restaurantAddress"
            value={newRestaurant.adresse}
            onChange={(e) => setNewRestaurant({ ...newRestaurant, adresse: e.target.value })}
            required
          />
        </div>
        {/* Add more input fields for other restaurant properties */}
        <button type="submit">Create</button>
      </form>

      {/* Ville Dropdown */}
      <div>
        <label htmlFor="ville">Ville:</label>
        <select
          id="ville"
          value={selectedVille}
          onChange={(e) => {
            const villeId = e.target.value;
            setSelectedVille(villeId);
            setSelectedZone('');
            fetchZones(villeId);
          }}
        >
          <option value="">Select Ville</option>
          {villes.map((ville) => (
            <option key={ville.id} value={ville.id}>
              {ville.name}
            </option>
          ))}
        </select>
      </div>

      {/* Zone Dropdown */}
      {selectedVille && (
        <div>
          <label htmlFor="zone">Zone:</label>
          <select
            id="zone"
            value={selectedZone}
            onChange={(e) => setSelectedZone(e.target.value)}
          >
            <option value="">Select Zone</option>
            {zones.map((zone) => (
              <option key={zone.id} value={zone.id}>
                {zone.name}
              </option>
            ))}
          </select>
        </div>
      )}

      {/* Update Restaurant Form */}
      {selectedRestaurant && (
        <div>
          <h2>Update Restaurant</h2>
          <form onSubmit={handleUpdateRestaurant}>
            <div>
              <label htmlFor="restaurantName">Name:</label>
              <input
                type="text"
                id="restaurantName"
                value={selectedRestaurant.nom}
                onChange={(e) =>
                  setSelectedRestaurant({ ...selectedRestaurant, nom: e.target.value })
                }
                required
              />
            </div>
            <div>
              <label htmlFor="restaurantAddress">Address:</label>
              <input
                type="text"
                id="restaurantAddress"
                value={selectedRestaurant.adresse}
                onChange={(e) =>
                  setSelectedRestaurant({ ...selectedRestaurant, adresse: e.target.value })
                }
                required
              />
            </div>
            {/* Add more input fields for other restaurant properties */}
            <button type="submit">Update</button>
          </form>
        </div>
      )}

      {/* Restaurant List */}
      {selectedVille && selectedZone && (
        <div>
          <h2>Restaurant List</h2>
          <table>
            <thead>
              <tr>
                <th>Number</th>
                <th>Name</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {restaurants.map((restaurant, index) => (
                <tr key={restaurant.id}>
                  <td>{index + 1}</td>
                  <td>{restaurant.nom}</td>
                  <td>
                    <button onClick={() => handleSelectRestaurant(restaurant)}>Edit</button>
                    <button onClick={() => handleDeleteRestaurant(restaurant.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Restaurants;
