import React, { useEffect, useState } from "react";
import { Table, Button, Modal, Form } from "react-bootstrap";
import axiosInstance from "../axiosInstance";
import Swal from 'sweetalert2';

function Restaurants() {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [updatedRestaurantName, setUpdatedRestaurantName] = useState("");
  const [newRestaurantName, setNewRestaurantName] = useState("");
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    fetchRestaurants();
  }, []);

  const fetchRestaurants = async () => {
    try {
      const response = await axiosInstance.get("/api/restaurants");
      const data = response.data;
      setRestaurants(data);
    } catch (error) {
      console.error("Error fetching restaurants:", error);
    }
  };

  const handleDeleteRestaurant = async (restaurantId) => {
    try {
      await axiosInstance.delete(`/api/restaurants/${restaurantId}`);
      fetchRestaurants();
    } catch (error) {
      console.error("Error deleting restaurant:", error);
    }
  };

  const handleUpdateRestaurant = (restaurant) => {
    setSelectedRestaurant(restaurant);
    setUpdatedRestaurantName(restaurant.nom);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setSelectedRestaurant(null);
    setUpdatedRestaurantName("");
  };

  const handleSaveChanges = async () => {
    try {
      if (selectedRestaurant) {
        const updatedRestaurant = { ...selectedRestaurant, nom: updatedRestaurantName };
        await axiosInstance.put(`/api/restaurants/${selectedRestaurant.id}`, updatedRestaurant);
        fetchRestaurants();
        Swal.fire({
          position: 'center',
          icon: 'success',
          title: 'Restaurant updated successfully',
          showConfirmButton: false,
          timer: 1500
        });
        handleCloseModal();
      }
    } catch (error) {
      console.error("Error updating restaurant:", error);
    }
  };

  const handleAddRestaurant = async () => {
    try {
      const newRestaurant = { nom: newRestaurantName };
      await axiosInstance.post("/api/restaurants", newRestaurant);
      fetchRestaurants();
      setNewRestaurantName("");
      Swal.fire({
        position: 'center',
        icon: 'success',
        title: 'Restaurant added successfully',
        showConfirmButton: false,
        timer: 1500
      });
    } catch (error) {
      console.error("Error adding restaurant:", error);
    }
  };

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
  };

  const filteredRestaurants = restaurants.filter((restaurant) =>
    restaurant.nom.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div>
      <h1>List of Restaurants</h1>

      {/* Add Restaurant Form */}
      <Form>
        <Form.Group>
          <Form.Label>Add a new restaurant</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter restaurant name"
            value={newRestaurantName}
            onChange={(e) => setNewRestaurantName(e.target.value)}
          />
        </Form.Group>
        <Button variant="primary" onClick={handleAddRestaurant}>
          Add Restaurant
        </Button>
      </Form>

      {/* Search Input */}
      <Form.Control
        type="text"
        placeholder="Search restaurants"
        value={searchQuery}
        onChange={handleSearch}
      />

      {/* Restaurant Table */}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredRestaurants.map((restaurant) => (
            <tr key={restaurant.id}>
              <td>{restaurant.id}</td>
              <td>{restaurant.nom}</td>
              <td>
                <Button variant="info" onClick={() => handleUpdateRestaurant(restaurant)}>
                  Update
                </Button>
                <Button variant="danger" onClick={() => handleDeleteRestaurant(restaurant.id)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Update Restaurant Modal */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Update Restaurant</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group>
            <Form.Label>Restaurant Name</Form.Label>
            <Form.Control
              type="text"
              value={updatedRestaurantName}
              onChange={(e) => setUpdatedRestaurantName(e.target.value)}
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Close
          </Button>
          <Button variant="primary" onClick={handleSaveChanges}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default Restaurants;
