import React from 'react';
import Logo from '../../pictures/Logo.png';
import axios from 'axios'; // Add this line to handle API requests
import { useHistory } from 'react-router-dom'; // Add this line to handle redirection
function UserHome() {
    const history = useHistory(); // Initialize useHistory to handle redirection
  
    // Define the logout function
    const logout = async () => {
      try {
        // Send a logout request to the server
        await axios.post('/api/v1/auth/logout');
        // Redirect the user to the login page or any other desired page
        history.push('/login');
      } catch (error) {
        // Handle any error that occurs during the logout process
        console.error('Error during logout:', error);
      }
    };
  
    // Define the home function if needed
    const home = () => {
      // Logic for handling the home button click
      // Add your code here
    };
  
    return (
      <div className="container-fluid" style={{ backgroundColor: '#7FC071' }}>
        <div className="row align-items-center">
          <nav className="navbar navbar-expand-lg navbar-light">
            <div className="col-sm">
              <img src={Logo} className="d-inline-block align-top" onClick={home} alt="" style={{ height: '20%', width: '20%' }} />
            </div>
  
            <div className="col-sm-3 d-flex justify-content-end" style={{ paddingRight: '20px' }}>
              <button type="button" className="btn btn-danger" onClick={logout}>Log Out</button>
            </div>
          </nav>
        </div>
      </div>
    );
  }
  
  export default UserHome;
