import React, { useEffect, useState } from "react";
import { Table, Form, Button, Modal } from "react-bootstrap";
import axiosInstance from "../axiosInstance";
import AdminHeader from "./AdminHeader";

function Zones() {
  const [cities, setCities] = useState([]);
  const [selectedCity, setSelectedCity] = useState("");
  const [zones, setZones] = useState([]);
  const [filteredZones, setFilteredZones] = useState([]);
  const [selectedZone, setSelectedZone] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [updatedZoneName, setUpdatedZoneName] = useState("");
  const [newZoneName, setNewZoneName] = useState("");
  const [newZoneCity, setNewZoneCity] = useState("");

  useEffect(() => {
    fetchCities();
    fetchAllZones();
  }, []);

  const fetchCities = async () => {
    try {
      const response = await axiosInstance.get("/api/villes");
      const data = response.data;
      setCities(data);
    } catch (error) {
      console.error("Error fetching cities:", error);
    }
  };

  const fetchAllZones = async () => {
    try {
      const response = await axiosInstance.get("/api/zones");
      const data = response.data;
      setZones(data);
      setFilteredZones(data);
    } catch (error) {
      console.error("Error fetching zones:", error);
    }
  };

  const fetchZonesByVille = async (villeName) => {
    try {
      const response = await axiosInstance.get("/api/zones/by-ville", {
        params: {
          villeName: villeName,
        },
      });
      const data = response.data;
      setFilteredZones(data);
    } catch (error) {
      console.error("Error fetching zones:", error);
    }
  };

  const handleCityChange = (e) => {
    const selectedCity = e.target.value;
    setSelectedCity(selectedCity);
    if (selectedCity === "") {
      setFilteredZones(zones);
    } else {
      fetchZonesByVille(selectedCity);
    }
  };

  const handleDeleteZone = (zone) => {
    // Set the selected zone for deletion
    setSelectedZone(zone);
  };

  const confirmDeleteZone = async () => {
    if (selectedZone) {
      try {
        await axiosInstance.delete(`/api/zones/${selectedZone.id}`);
        setFilteredZones((prevZones) =>
          prevZones.filter((zone) => zone.id !== selectedZone.id)
        );
        setSelectedZone(null);
      } catch (error) {
        console.error("Error deleting zone:", error);
      }
    }
  };

  const handleUpdateZone = (zone) => {
    // Set the selected zone for update
    setSelectedZone(zone);
    setUpdatedZoneName(zone.nom);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleSaveUpdate = async () => {
    if (selectedZone && updatedZoneName.trim() !== "") {
      try {
        const updatedZone = { ...selectedZone, nom: updatedZoneName };
        await axiosInstance.put(`/api/zones/${selectedZone.id}`, updatedZone);
        setFilteredZones((prevZones) =>
          prevZones.map((zone) =>
            zone.id === selectedZone.id ? updatedZone : zone
          )
        );
        setSelectedZone(null);
        setShowModal(false);
      } catch (error) {
        console.error("Error updating zone:", error);
      }
    }
  };

  const handleAddZone = async (e) => {
    e.preventDefault();
    if (newZoneName.trim() !== "" && newZoneCity !== "") {
      try {
        const selectedCity = cities.find((city) => city.nom === newZoneCity);
        const newZone = {
          nom: newZoneName,
          villeId: selectedCity.id,
        };
        await axiosInstance.post("/api/zones", newZone);
        setNewZoneName("");
        setNewZoneCity("");
        fetchAllZones();
      } catch (error) {
        console.error("Error adding zone:", error);
      }
    }
  };
  

  return (
    <>
      <AdminHeader />

      <div className="container">
        <h1 className="mt-3" style={{ fontFamily: "cursive" }}>
          Zones
        </h1>

        <div>
          <Form.Group>
            <Form.Label className="alert alert-warning">
              Sélectionnez une ville pour filtrer les zones:
            </Form.Label>
            <Form.Control
              as="select"
              onChange={handleCityChange}
              className="mb-4"
            >
              <option value="">Select a city</option>
              {cities.map((city) => (
                <option key={city.id} value={city.nom}>
                  {city.nom}
                </option>
              ))}
            </Form.Control>
          </Form.Group>
        </div>

        {filteredZones.length > 0 && (
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredZones.map((zone) => (
                <tr key={zone.id}>
                  <td>{zone.id}</td>
                  <td>{zone.nom}</td>
                  <td>
                    <Button
                      onClick={() => handleUpdateZone(zone)}
                      style={{ backgroundColor: "#F28135" }}
                    >
                      Update
                    </Button>{" "}
                    <Button
                      variant="danger"
                      onClick={() => handleDeleteZone(zone)}
                    >
                      Delete
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        )}

        {/* Modal for zone update */}
        <Modal show={showModal} onHide={handleCloseModal}>
          <Modal.Header closeButton>
            <Modal.Title>Update Zone</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form.Group>
              <Form.Label>Zone Name:</Form.Label>
              <Form.Control
                type="text"
                value={updatedZoneName}
                onChange={(e) => setUpdatedZoneName(e.target.value)}
              />
            </Form.Group>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleCloseModal}>
              Close
            </Button>
            <Button variant="primary" onClick={handleSaveUpdate}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>

        <div>
          <h2>Add New Zone</h2>
          <Form onSubmit={handleAddZone}>
            <Form.Group>
              <Form.Label>Zone Name:</Form.Label>
              <Form.Control
                type="text"
                value={newZoneName}
                onChange={(e) => setNewZoneName(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>City:</Form.Label>
              <Form.Control
                as="select"
                value={newZoneCity}
                onChange={(e) => setNewZoneCity(e.target.value)}
              >
                <option value="">Select a city</option>
                {cities.map((city) => (
                  <option key={city.id} value={city.nom}>
                    {city.nom}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
            <Button type="submit">Add Zone</Button>
          </Form>
        </div>
      </div>
    </>
  );
}

export default Zones;
